#include<iostream>
using namespace std; 
int main(){
    int n;
    cout<<"Enter a Number : ";
    cin>>n;
    int count = 0; 
    int a = n;
    while(n>0){
        n/=10;
        count++;
    }
    if(a == 0) count<<1;
    else cout<<count;
}                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                       
