#include<iostream>
using namespace std; 
int main(){
    int n;
    cout<<"Enter a Number : ";
    cin>>n;
    int lastdigit = 0;
    int rev =0;
    while(n>0){
        rev*=10;
        lastdigit = n%10;
        rev+= lastdigit;
        n/= 10;
    }
    cout<<rev;
}                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                       
